import { useDispatch, useSelector } from 'react-redux';
import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import { ProductDataAction } from '../../store/ProductDataSlice';

const ProductItem = (props) => {
  const productdata = useSelector(state=>state.data.productData);
  console.log(productdata);

  const dispatch = useDispatch();
  const addtoCartHandler = () =>{

     dispatch(ProductDataAction.addProductData(productdata));
  }

  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{productdata.title}</h3>
          <div className={classes.price}>${productdata.price.toFixed(2)}</div>
        </header>
        <p>{productdata.description}</p>
        <div className={classes.actions}>
          <button onClick={addtoCartHandler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
