import { useDispatch, useSelector } from 'react-redux';
import classes from './CartItem.module.css';
import {ProductDataAction} from '../../store/ProductDataSlice';

const CartItem = () => {
  const productData = useSelector((state) => state.productdata.ProductData);
  const dispatch = useDispatch();

  return (
    <ul>
      {productData.map((product) => (
        <li key={product.id} className={classes.item}>
          <header>
            <h3>{product.title}</h3>
            <div className={classes.price}>
              ${(product.price * product.quantity).toFixed(2)}{' '}
              <span className={classes.itemprice}>
                (${product.price.toFixed(2)}/item)
              </span>
            </div>
          </header>
          <div className={classes.details}>
            <div className={classes.quantity}>
              x <span>{product.quantity}</span>
            </div>
            <div className={classes.actions}>
              <button onClick={() => dispatch(ProductDataAction.decreaseQuantity({ id: product.id }))}>-</button>
              <button onClick={() => dispatch(ProductDataAction.increaseQuantity({ id: product.id }))}>+</button>
            </div>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default CartItem;


