import { useDispatch, useSelector } from 'react-redux';
import classes from './CartItem.module.css';
import { ProductDataAction } from '../../store/ProductDataSlice';

const CartItem = () => {
  const ProductData = useSelector((state) => state.data.ProductData);
  const quantity = useSelector((state) => state.data.quantity);
  const dispatch = useDispatch();

  const decreaseQuantityHandler = (id) => {
    dispatch(ProductDataAction.decreaseQuantity({ id }));
  };

  const increaseQuantityHandler = (id) => {
    dispatch(ProductDataAction.increaseQuantity({ id }));
  };

  if (!ProductData || ProductData.length === 0) {
    return <p>No items in the cart.</p>;
  }

  return (
    <ul>
      {ProductData.map((product, index) => (
        <li key={index} className={classes.item}>
          <header>
            <h3>{product.title}</h3>
            <div className={classes.price}>
              ${(product.price * product.quantity).toFixed(2)}{' '}
              <span className={classes.itemprice}>
                (${product.price.toFixed(2)}/item)
              </span>
            </div>
          </header>
          <div className={classes.details}>
            <div className={classes.quantity}>
              x <span>{product.quantity}</span>
            </div>
            <div className={classes.actions}>
              <button onClick={() => decreaseQuantityHandler(product.id)}>-</button>
              <button onClick={() => increaseQuantityHandler(product.id)}>+</button>
            </div>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default CartItem;
