import { createSlice } from "@reduxjs/toolkit";

const initialCartDataState = {
    ProductData: [],
    quantity: 0,
    totalamount: 0,
}

const ProductDataSlice = createSlice({
    name: 'productdata',
    initialState: initialCartDataState,
    reducers: {
        addProductData(state, action) {
           if(state.ProductData.id ===action.payload.id){
               state.quantity = state.quantity+1;
               state.totalamount = state.totalamount*state.quantity;
           }else{
            state.ProductData = action.payload;
           }
        },
        increaseQuantity(state, action) {
            state.quantity = state.quantity + action.payload;
            state.totalamount = state.quantity * 6;
        },
        decreaseQuantity(state, action) {
            if (state.quantity > 0) {
                state.quantity = state.quantity - action.payload;
                state.totalamount = state.quantity * 6;
            } else {
                state.quantity = 0;
                state.totalamount = 0;
            }
        },
    }
})

console.log(initialCartDataState);

export const ProductDataAction = ProductDataSlice.actions;
export default ProductDataSlice.reducer;
