import { useDispatch, useSelector } from 'react-redux';
import classes from './CartItem.module.css';
import { ProductDataAction } from '../../store/ProductDataSlice';

const CartItem = (props) => {
  const dispatch = useDispatch();

  const { title, quantity, total, price, id } = props.item;

  const removeItemHandler = () => {
    dispatch(ProductDataAction.removeItemFromCart(id));
  };

  const addItemHandler = () => {
    dispatch(ProductDataAction.addProductData({
      id,
      title,
      price,
    }))
  }

  return (
        <li className={classes.item}>
          <header>
            <h3>{props.title}</h3>
            <div className={classes.price}>
              ${(props.totalamount).toFixed(2)}{' '}
              <span className={classes.itemprice}>
                (${props.price.toFixed(2)}/item)
              </span>
            </div>
          </header>
          <div className={classes.details}>
            <div className={classes.quantity}>
              x <span>{props.quantity}</span>
            </div>
            <div className={classes.actions}>
              <button onClick={removeItemHandler}>-</button>
              <button onClick={addItemHandler}>+</button>
            </div>
          </div>
        </li>
  );
};

export default CartItem;
