import { createSlice } from "@reduxjs/toolkit";
import { UIAction } from "./UISlice";

const initialCartDataState = {
    ProductData: [],
    totalQuantity: 0,
}

const ProductDataSlice = createSlice({
    name: 'productdata',
    initialState: initialCartDataState,
    reducers: {
        replaceCart(state, action) {
            console.log(action.payload);
            state.totalQuantity = action.payload.totalQuantity;
            state.ProductData = action.payload.items;
        },

        addProductData(state, action) {
            const newItem = action.payload;
            const existingItem = state.ProductData.find((item) => item.id === newItem.id);
            state.totalQuantity++;
            if (!existingItem) {
                state.ProductData.push({
                    id: newItem.id,
                    price: newItem.price,
                    quantity: 1,
                    totalPrice: newItem.price,
                    name: newItem.title,
                });
            } else {
                existingItem.quantity++;
                existingItem.totalPrice = existingItem.totalPrice + newItem.price;
            }
        },

        removeItemFromCart(state, action) {
            const id = action.payload;
            const existingItem = state.ProductData.find((item) => item.id === id);
            state.totalQuantity--;
            if (existingItem.quantity === 1) {
                state.ProductData = state.ProductData.filter((item) => item.id !== id);
            } else {
                existingItem.quantity--;
            }
        },
    }
})

export const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(UIAction.showNotification({
            status: 'pending',
            title: 'pending...',
            message: 'Sending cart data!',
        }))

        const sendRequest = async () => {
            const response = await fetch('https://cart-http-request-default-rtdb.firebaseio.com/cart.json', {
                method: "POST",
                body: JSON.stringify(cart)
            })
            if (!response.ok) {
                throw new Error('Sent cart data failed!');
            }
        }
        try {
            await sendRequest();
            dispatch(UIAction.showNotification({
                status: 'success',
                title: 'Success!',
                message: 'Sent cart data successfully!',
            }))
        } catch (err) {
            dispatch(UIAction.showNotification({
                status: 'error',
                title: 'Error!',
                message: 'Sent cart data failed!',
            }))
        }


    }
}


export const ProductDataAction = ProductDataSlice.actions;
export default ProductDataSlice.reducer;
