import { createSlice } from "@reduxjs/toolkit";

const initialCartDataState = {
    ProductData : [],
    quantity : 0,
    totalamount : 0,
}

const ProductDataSlice = createSlice({
    name : 'productdata',
    initialState : initialCartDataState,
    reducers : {
        addProductData (state,action){
            console.log(state.ProductData.title)
              // Check if the product already exists in the cart
            //   const existingProductIndex = state.ProductData.findIndex(
            //     (product) => product.title === action.payload.title
            // );
            // if (existingProductIndex >= 0) {
            //     // If it exists, update the quantity
            //     state.ProductData[existingProductIndex].quantity += 1;
            // } else {
            //     // If it doesn't exist, add it with an initial quantity
            //     state.ProductData.push({ ...action.payload, quantity: 1 });
            // }
        },
        increaseQuantity(state,action){
           state.quantity = state.quantity+action.payload;
           state.totalamount =  state.quantity*6;
        },
        decreaseQuantity(state,action){
            if(state.quantity>0){
                state.quantity = state.quantity-action.payload;
                state.totalamount = state.quantity*6;
            }else{
                state.quantity = 0;
                state.totalamount = 0;
            }
        },
    }
})

export const ProductDataAction = ProductDataSlice.actions;
export default ProductDataSlice.reducer;
