import { useDispatch, useSelector } from 'react-redux';
import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import { ProductDataAction } from '../../store/ProductDataSlice';

const ProductItem = ( { title, price, description ,id}) => {

  const dispatch = useDispatch();
  const cart = useSelector((state)=>state.data);
  console.log(cart);
  // const productData = { title, price, description ,id};

  const addtoCartHandler = () =>{
    const newTotalQuantity = cart.totalQuantity + 1;

    const updatedItems = cart.ProductData.slice(); // create copy via slice to avoid mutating original state  
    const existingItem = updatedItems.find((item) => item.id === id);
    if (existingItem) {
      const updatedItem = { ...existingItem }; // new object + copy existing properties to avoid state mutation
      updatedItem.quantity++;
      updatedItem.totalPrice = updatedItem.totalPrice + price;
      const existingItemIndex = updatedItems.findIndex(
        (item) => item.id === id
      );
      updatedItems[existingItemIndex] = updatedItem;
    } else {
      updatedItems.push({
        id: id,
        price: price,
        quantity: 1,
        totalPrice: price,
        name: title,
      });
    }

    const newCart = {
      totalQuantity: newTotalQuantity,
      items: updatedItems,
    };

    dispatch(ProductDataAction.replaceCart(newCart));

    //  dispatch(ProductDataAction.addProductData(productData));
  }
  return (
    <li key={id} className={classes.item} >
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={addtoCartHandler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
