import { useDispatch, useSelector } from 'react-redux';
import classes from './CartItem.module.css';
import {ProductDataAction} from '../../store/ProductDataSlice';

const CartItem = (props) => {
  const ProductData = useSelector(state=>state.data.ProductData);
  const quantity = useSelector(state=>state.data.quantity);
  const totalamount = useSelector(state=>state.data.totalamount);
  const dispatch = useDispatch();

  const decreaseQuantityHandler = () =>{
       dispatch(ProductDataAction.decreaseQuantity(1));
      };

      const increaseQuantityHandler = () =>{
        dispatch(ProductDataAction.increaseQuantity(1));
  };

  return (

    <ul>
      {ProductData.map((product,index) => (
        <li key={index} className={classes.item}>
          <header>
            <h3>{product.title}</h3>
            <div className={classes.price}>
              ${(product.price && product.quantity ? (product.price * product.quantity).toFixed(2) : '0.00')}{' '}
              <span className={classes.itemprice}>
                (${product.price ? product.price.toFixed(2) : '0.00'}/item)
              </span>
            </div>
          </header>
          <div className={classes.details}>
            <div className={classes.quantity}>
              x <span>{quantity}</span>
            </div>
            <div className={classes.actions}>
              <button onClick={() => dispatch(ProductDataAction.decreaseQuantity({ id: product.id }))}>-</button>
              <button onClick={() => dispatch(ProductDataAction.increaseQuantity({ id: product.id }))}>+</button>
            </div>
          </div>
        </li>
      ))}
    </ul>


    // <li className={classes.item}>
